import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "./styles.css";

function Form() {
	const [testUrl, setTestUrl] = useState("");
	const [data, setData] = useState('');
	const [name, setName] = useState('fetching data ...');

	async function getData(url){
		var url = url.replace('https://', '');

		const result = await axios('https://webpagetest.org/jsonResult.php?test=200815_B4_41374733eeb3724fb0cd3a57637712b7&pretty=1');
		setName(result.data.data.url);

	}



	return (
		<div>
			<div className="row">
				<div className="input-field col s12">
					<input onChange={(event)=> {setTestUrl(event.target.value)}} id="email" type="text" className="validate" />
					<label htmlFor="email">Webpagetest URL</label>
				</div>
			</div>

			<button onClick={ ()  => getData(testUrl) } className="btn waves-effect waves-light" type="submit" name="action">
				Submit
			</button>

		<h1>NAME! = {name}</h1>

		</div>
	);
}

export default function App() {
	return (
		<div className="App">
			<Form />
		</div>
	);
}
